import { CSSTransition } from 'react-transition-group';
import '../../App.css'
import React, { useState } from 'react';

function TextBan() {

    const [isVisible, setIsVisible] = useState(true);
    const [isButtonVisible, setIsButtonVisible] = useState(true);
  
    const handleClose = () => {
      setIsVisible(false);
      setIsButtonVisible(false);
    };

    return (

      <>
       <div className="al">
       <div>
      {isVisible && (
        <div className="alert" style={{ background:'black', border: '1px solid black', padding: '20px', marginBottom: '10px' }}>
          Это окно. Нажмите кнопку, чтобы закрыть.
        </div>
      )}
      {isButtonVisible && (
        <button className="bu"  onClick={handleClose}>x</button>
      )}
    </div>

        </div>
        </>
  );
}

export default TextBan;
