import '../Search/Seach.css'
import React, { useState } from 'react';
import { useHistory } from 'react-router';

const Search = () => {

  const [query, setQuery] = useState('');
  const history = useHistory();

  const handleSearch = (e) => {
    e.preventDefault();
    if (query.trim()) {
      history.push(`/search?query=${encodeURIComponent(query)}`);
    }
  };
    return (
    <div className='searcH'>
       <form onSubmit={handleSearch}>
        <input  type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)} placeholder='Поиск...' className='inp1O1' type="text" title="Разрешено использовать только пробелы и русские буквы"
            pattern="^[А-Яа-яЁё\s]+$" maxLength={10} name="us_name" required
          />
          </form>
    </div>
    )
}
 
export default Search;
